mapper.select:
SELECT comment_id commentId, post_id postId, (select username from user where user.uuid = c.postuser_id) postUserName, (select username from user where user.uuid = c.commentuser_id) commentUserName, (select username from user where user.uuid = c.acceptuser_id) acceptUserName, comment_text commentText, DATE_FORMAT(comment_time, '%Y.%m.%d %T') commentTime, comment_state commentState FROM comment c
comment_text LIKE concat('%',#{commentText},'%')
ORDER BY postuser_id LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE comment
SET
comment_state = #{commentState}
WHERE comment_id = #{commentId}

mapper.select:
SELECT c.comment_id,c.post_id,c.postuser_id,c.acceptuser_id,c.comment_text,
c.comment_time,c.comment_state FROM comments c
WHERE commentuser_id = #{commentuserId}
ORDER BY comment_time LIMIT #{offSet}, #{pageRow}

SELECT c.comment_id,c.post_id,c.postuser_id,c.commentuser_id,c.comment_text,
c.comment_time,c.comment_state FROM comments c
WHERE acceptuser_id = #{acceptUserId}
ORDER BY comment_time LIMIT #{offSet}, #{pageRow}
