mapper.select:
SELECT w.categories_id id, w.categories_name sortname, w.categories_img imageUrl, w.status status, date_format(w.create_time, '%Y.%m.%d %T') createTime, date_format(w.update_time, '%Y.%m.%d %T') updateTime FROM post_categories w
w.categories_id = #{selectId}
ORDER BY w.categories_id DESC LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE post_categories
SET
    categories_name = #{sortname},
    categories_img = #{imageUrl}
WHERE categories_id = #{id}
